mapper.select:
select

,

,

from blog b
JOIN user u on b.user_id = u.id
where b.is_deleted = 0
 and b.created_date <= now() 

    and b.user_id = #{blog.userId, jdbcType=INTEGER}


    and b.title like CONCAT('%',#{blog.title,jdbcType=VARCHAR},'%')


    and b.status = #{blog.status, jdbcType=INTEGER}


    and b.id in(
      select r.blog_id from blog_rel_category r
      where r.category_tag_id = #{blog.categroyId, jdbcType=INTEGER}
    )

order by b.created_date desc LIMIT #{page.offset, jdbcType=INTEGER}, #{page.limit, jdbcType=INTEGER}

select
,
from blog b JOIN user u on b.user_id = u.id where b.is_deleted = 0 and u.id = #{userId,jdbcType=INTEGER} and b.type = #{type,jdbcType=INTEGER}

select
,
from blog b where b.id = #{id,jdbcType=INTEGER}
